from flask import Flask, render_template

app = Flask(__name__)

@app.route("/") #bh cx di voi line 6: neu user truy cap trang chu thi home se chay
def home():
    return "Hello Flask"

@app.route("/c4e")
def c4e():
    return "Hello c4e, hihi"

@app.route("/me")
def me():
    return "Nguyen Nam Dan"

@app.route("/hi/<name>")
def hiName(name):
    return "hi " + name

@app.route("/add/<int:x>/<int:y>")
def summ(x, y):
    return str(x + y)  

@app.route("/posts/<int:index>") 
def posts(index): 
    listTitle = ["Hom nay 16/11", "haha"]
    listContent = ["Viet Nam 2-0 Malaysia", "hihi"]
    return render_template("posts.html", title= listTitle[index], content= listContent[index]) 

@app.route("/movie")
def movie():
    return render_template("movie.html", name= "harry potter", image= "https://www.irishtimes.com/polopoly_fs/1.3170107.1501253408!/image/image.jpg_gen/derivatives/box_620_330/image.jpg") 

